import { useEffect, useState } from "react";
import { fetchData } from "../../API";

export const Body = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    fetchData(setData);
  }, []);

  data && console.log(data);

  function unicodetoChar(String) {
    const doc = new DOMParser().parseFromString(String, "text/html");
    return doc.documentElement.textContent;
  }
  return (
    <div className="bg-coffee-l px-1">
      {data &&
        data.map((entry) => (
          <div className="flex items-cen">
            <li className="list-decimal" key={entry.title}>
              <div className="flex">
                <div className="h-0 w-0 border-[12px] border-x-[5px] border-transparent border-b-grey-n"></div>
                <div>
                  {entry.title ? unicodetoChar(entry.title) : "Title " + entry.id}
                </div>
              </div>
            </li>
          </div>
        ))}
    </div>
  );
};
