import { useEffect, useState } from "react";
import { fetchId, mapUrlById, stories } from "../../API/fetch";

export const Body = () => {
  const [story, setStory] = useState();
  useEffect(() => {
    fetchId().then((id) => {
      mapUrlById(id);
      setStory(stories);
    });
  }, []);

  stories && console.log(stories);
  function unicodetoChar(String) {
    const doc = new DOMParser().parseFromString(String, "text/html");
    return doc.documentElement.textContent;
  }
  return (
    <div className=" px-1">
      {stor &&
        stories.map((entry, index) => (
          <li className="list-none list-item" key={entry.id}>
            <div className="inline-block w-6 text-right">{index + 1}.</div>
            <div className="inline-block cursor-pointer mx-3 h-0 w-0 border-[12px] border-x-[5px] border-transparent border-b-grey-n"></div>

            {entry.title && entry.url ? (
              <div className="inline-block ">
                <div className="inline-block ">
                  {unicodetoChar(` ${entry.title}  `)}
                </div>
                <div className="inline-block text-[70%] text-grey-n">
                  <a href={entry.url}>
                    &nbsp;{`(${new URL(entry.url).hostname})`}
                  </a>
                </div>
              </div>
            ) : (
              <div className="inline-block ">{"Title " + entry.id}</div>
            )}
          </li>
        ))}
    </div>
  );
};
