import React from "react";

function Loading() {
  const randomWidth = 200 + Math.floor(Math.random() * 200);
  const width = `w-[${randomWidth}px] `;
  console.log(wid);
  return (
    <div className="animate-pulse flex">
      <div className="w-6 h-6 bg-stone-200 rounded-md my-2 ml-4"></div>
      <div className="flex flex-col gap-2">
        <div
          className={`${width} h-3 bg-stone-200 rounded-full mt-2 mx-4`}
        ></div>
        <div className="w-60 h-3 bg-stone-200 rounded-full mb-2 mx-4"></div>
      </div>
    </div>
  );
}

export default Loading;
