import React from "react";
import { useState } from "react";

function Time(props) {
  const unixTimeStamp = props.time;
  const convertedTime = new Date(unixTimeStamp * 1000).getMinutes();
  const currentTime = new Date().getMinutes();
  const relativeTime = Math.abs(currentTime - convertedTime);

  return irelativeTime < 60 ? <div>{relativeTime + " minutes ago"}</div> : <div></div>;
}

export default Time;
