async function fetchData(id, setData, set) {
  const response = await fetch(
    `https://hacker-news.firebaseio.com/v0/item/${id}.json?print=pretty`
  );
  const result = await response.json();
  let dataArr = [];
  dataArr.push(result);
  setData(dataArr);
}

export { fetchData };
