import React from "react";
import InputField from "./InputField";
import InputButton from "./InputButton";
function LoginOrRegister(props) {
  return (
    <div>
      <span className="text-lg font-bold">{props.name[0].toUpperCase()}</span>
      <div className="flex w-fit items-end flex-col gap-2 mt-5">
        <InputField name={"username:"} />
        <InputField name={"password:"} />
      </div>
      <InputButton name={props.name.toLowerC} />

      {props.forgotPassword && (
        <p className="text-purple-n text-xl mt-3 underline">
          Forgot your password?
        </p>
      )}
    </div>
  );
}

export default LoginOrRegister;
