import React, { useState, useEffect } from "react";
import { Header } from "../home/header/Header";
import { useLocation } from "react-router-dom";
function AboutAuthor() {
  const location = useLocation();
  const [userInfo, setUserInfo] = useState();
  const date = userInfo && new Date(userInfo.created );
  async function fetchUserDetails() {
    const response = await fetch(
      `https://hacker-news.firebaseio.com/v0/user/${location.state.id}.json`
    );
    setUserInfo(await response.json());
  }
  useEffect(() => {
    fetchUserDetails();

  }, []);

  return (
    <div>
      <Header />
      <div className="flex">
          <div className="bg-coffee-l flex flex-col font-verdana">
              <span>user:{userInfo && userInfo.id}</span>
              <span>created:</span>
              <span>karma:{userInfo && userInfo.karma}</span>
              <div className="flex">
                  <span>about:</span>
                  <div className="flex flex-col mt-4">
                    <span>submissions</span>
                    <span>comments</span>
                    <span>favorites</span>
                  </div>
              </div>
      </div>
      <div className="f">
        <span></span>
        <span></span>
        <span></span>
      </div>
      </div>
    </div>
  );
}

export default AboutAuthor;
