import React from "react";
import { Header } from "../home/header/Header";
import TopStory from "../home/body/topstory/TopStory";
import { useParams } from "react-router-dom";

function CommentsMain() {
  const params = useParams();
  console.log(params);
  return (
    <div className="px-28 py-3">
      <Header />
      <div className="bg-coffee-l pl-4 pt-4 font-verdana">
        <TopStory id={params.id} />
        <div>
            <textarea className="resize w-1/2 border border-black rounded-sm"></textarea>
            <p>
              If you haven't already, would you mind reading about HN's approach to
              comments and site guidelines?
            </p>
            <button>add comment</button>
        </div>
      </div>
    </div>
  );
}

export default CommentsMain;
