import React, { useState, useEffect } from "react";
import { Header } from "../home/header/Header";
import { useLocation } from "react-router-dom";
function AboutAuthor() {
  const location = useLocation();
  const [userInfo, setUserInfo] = useState();
  const date = userInfo && userInfo.created;
  async function fetchUserDetails() {
    const response = await fetch(
      `https://hacker-news.firebaseio.com/v0/user/${location.state.id}.json`
    );
    setUserInfo(await response.json());
  }
  useEffect(() => {
    fetchUserDetails();

  }, []);

  return (
    <div>
      <Header />
      <div className="flex bg-coffee-l">
        <div className="flex flex-col">
          <span>user:{userInfo && userInfo.id}</span>
          <span>created:</span>
          <span>karma:{userInfo && userInfo.karma}</span>
          <span>about:</span>
        </div>
        <div className="flex flex-col"></div>
      </div>
    </div>
  );
}

export default AboutAuthor;
