import React, { useState, useEffect } from "react";
import { Header } from "../home/header/Header";
import { useLocation } from "react-router-dom";
function AboutAuthor() {
  const location = useLocation();
  const [userInfo, setUserInfo] = useState();
  const date = userInfo && new Date(userInfo.created * 1000);
  var day = ('0' + date.getDate()).slice(-2);
    var monthIndex = date.getMonth();
    var year = date.getFullYear();
  async function fetchUserDetails() {
    const response = await fetch(
      `https://hacker-news.firebaseio.com/v0/user/${location.state.id}.json`
    );
    setUserInfo(await response.json());
  }
  useEffect(() => {
    fetchUserDetails();
  }, []);

  return (
    <div className="px-28 py-3">
      <Header />
      <div className="flex gap-1 bg-coffee-l text-grey-n px-1 pt-4 pb-10">
        <div className=" flex flex-col font-verdana">
          <span>user:</span>
          <span>created:</span>
          <span>karma:</span>
          <div className="flex">
            <span>about:</span>
          </div>
        </div>
        <div className="flex flex-col">
          <span>{userInfo && userInfo.id}</span>
          <span>3</span>
          <span>{userInfo && userInfo.karma}</span>
          <div className="flex flex-col mt-4 text-black underline">
            <span>submissions</span>
            <span>comments</span>
            <span>favorites</span>
          </div>
        </div>
      </div>
    </div>
  );
}

export default AboutAuthor;
