  const response = await fetch(
    "https://hacker-news.firebaseio.com/v0/topstories.json"
  );
  const results = await response.json();
  //console.log(results);
  results.forEach((id) => {
    fetchData(id,se);
  });
  setData(story);
}

async function fetchData(id) {
  const response = await fetch(
    `https://hacker-news.firebaseio.com/v0/item/${id}.json`
  );
  const results = await response.json();
  story.push(results);
}
