import { useEffect, useState } from "react";
import { fetchData } from "../../API";

export const Body = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    // for (let id = 1; id < 30; id++) {
    // }
    fetchData(setData);
  }, []);

  data && console.log(data);

  function unicodetoChar(String) {
    const doc = new DOMParser().parseFromString(String, "text/html");
    return doc.documentElement.textContent;
  }
  return (
    <div className="bg-coffee-l px-1">
      {data &&
        data.map((entry, index) => (
          <li className="list-none list-item" key={entry.id}>
            <div className="inline-block w-6 text-right">{index + 1}.</div>
            <div className="inline-block cursor-pointer mx-3 h-0 w-0 border-[12px] border-x-[5px] border-transparent border-b-grey-n"></div>
            <div className="inline-block ">
              {entry.title && entry.url
                ? unicodetoChar(
                    ` ${entry.title} (${(
                      <span className='te'>new URL(entry.url).hostname</span>
                    )})`
                  )
                : "Title " + entry.id}
            </div>
          </li>
        ))}
    </div>
  );
};
