async function fetchData(setData) {
  setData([]);
  var count = 0;
  while (count < 30) {

    console.log(id);
    const response = await fetch(
      `https://hacker-news.firebaseio.com/v0/item/${id}.json`
    );
    const result = await response.json();
    if (result.type === "story") {
      count++;
      setData((prev) => [...prev, result]);
    }
  }
}

export { fetchData };
