export async function sampleData(setData) {
  const stories = [];
  const response = await fetch(
    "https://hacker-news.firebaseio.com/v0/topstories.json"
  );
  const results = await response.json();
  setData(results)

  //   results.map((id) => {
  //     stories.push(fetchData(id, stories));
  //   });
  //   setData(stories);
  //   console.log(stories);
}

export async function fetchData(id, setData) {
  const response = await fetch(
    `https://hacker-news.firebaseio.com/v0/item/${id}.json`
  );
  const result=await response.json();
  setData(...result)
}
