export async function fetchId() {
  const response = await fetch(
    "https://hacker-news.firebaseio.com/v0/topstories.json"
  );
  const id = await response.json();
  return id;
}
export async function mapUrlById(id) {
  const url = id;
  console.log(url[0]);
  url.map((item) => (async function (item) {
    const response = await fetch(
      `https://hacker-news.firebaseio.com/v0/item/${ite}.json`
    );
    const id = await response.json();
    return id;
  })());
}
// get the array of ids
// then for each element in the ids array map urls to a state
// fetch data from the state and pass it in the body
