import React, { useEffect, useState } from "react";
import { fetchData } from "../../API";

export const Body = () => {
  var id = 0;
  const [data, setData] = useState([]);
  const stories = [];
  useEffect(() => {
    while (stories.length < 30) {
      id++;

      fetchData(setData, id);
      if (data.type === "story") {
        stories.push(data);
      }
    }
    log
  }, []);
  data && console.log(data);

  // return <div className="bg-coffee-l">{data && data.title}</div>;
};
