import React from "react";

const Time = ({ time }) => {
  const convertedDate = new Date(time * 1000);
  const currentTime = new Date();
  const diffTime = Math.abs(currentTime - convertedDate);
  const diffMinutes = Math.floor((diffTime / (1000 * 60)) % 60);
  const diffHours = Math.floor((diffTime / (1000 * 60 * 60)) % 24);
  const diffDays = Math.floor(diffTime / (1000 * 60 * 60 * 24));
  const diffMonths = Math.floor(diffTime / (1000 * 60 * 60 * 24 * 30));


  const Months = [
    "Jan",
    "Feb",
    "Mar",
    "Apr",
    "May",
    "Jun",
    "Jul",
    "Aug",
    "Sep",
    "Oct",
    "Nov",
    "Dec",
  ];

  if (diffMinutes < 60) {
    return <div>{diffMinutes} minutes ago</div>;
  } else if (diffHours < 24) {
    return <div>{diffHours} hours ago</div>;
  } else if (diffDays < 30) {
    return <div>{diffDays} days ago</div>;
  } else if (diffMonths < 12) {
    return <div>{diffMonths} months ago</div>;
  } else {
    return (
      <div>
        on {Months[convertedDate.getMonth()]} {convertedDate.getFullYear()}
      </div>
    );
  }
};

export default Time;
