import React from "react";
import { useState } from "react";

function Time(props) {
  const unixTimeStamp = props.time;
  const convertedTime = new Date(unixTimeStamp * 1000).getMinutes();
  const currentTime = new Date().getMinutes();
  const relativeTime = Math.abs(currentTime - convertedTime);
  const Months = [
    "Jan",
    "Feb",
    "Mar",
    "Apr",
    "May",
    "Jun",
    "Jul",
    "Aug",
    "Sep",
    "Oct",
    "Nov",
    "Dec",
  ];
  if (relativeTime < 60) {
    return <div>{relativeTime + " minutes ago"}</div>;
  } else if (relativeTime >= 60) {
    if (Math.floor(relativeTime / 60) >= 24) {
      if (Math.floor(relativeTime / 60 / 24) >= 30) {
        if (Math.floor(relativeTime / 60 / 24 / 30 / 12) >= 365) {
          return <div>{"on"+convertedTime}</div>;
        }
        return (
          <div>{Math.floor(relativeTime / 60 / 24 / 30) + " months ago"}</div>
        );
      }
      return <div>{Math.floor(relativeTime / 60 / 24) + " days ago"}</div>;
    }
    return <div>{Math.floor(relativeTime / 60) + " hours ago"}</div>;
  }
}

export default Time;
